DROP DATABASE IF EXISTS planner;CREATE DATABASE planner;USE planner;/*======================================CREATE STATEMENTS======================================*/CREATE TABLE Recipe(recipeID int auto_increment,creationDate date default CURRENT_DATE,recipeName varchar(255),preparationTime int,primary key(recipeID));CREATE TABLE Meal(mealID int auto_increment,inputServing int,imageUpload varchar(255),calorieCount int,primary key(mealID));CREATE TABLE MealPlan(mealPlanID int auto_increment,primary key(mealPlanID));CREATE TABLE User(userID int auto_increment,fname varchar(50),lname varchar(50),username varchar(150),password varchar(255),primary key(userID));CREATE TABLE Ingredient(ingredientID int auto_increment,ingredientName varchar(255),primary key(ingredientID));CREATE TABLE Measurement(measurementID int auto_increment,quantity varchar(10),unit varchar(50),primary key(measurementID));CREATE TABLE Instruction(recipeID int,stepNumber int,direction varchar(255),primary key(recipeID, stepNumber),foreign key(recipeID) references Recipe(recipeID) on delete cascade);CREATE TABLE includes(mealPlanID int,mealID int,primary key(mealPlanID, mealID),foreign key(mealPlanID) references MealPlan(mealPlanID) on delete cascade,foreign key(mealID) references Meal(mealID) on delete cascade);CREATE TABLE has(mealPlanID int,userID int,primary key(mealPlanID, userID),foreign key(mealPlanID) references MealPlan(mealPlanID) on delete cascade,foreign key(userID) references User(userID) on delete cascade);CREATE TABLE kitchen(ingredientID int,userID int,primary key(ingredientID, userID),foreign key(ingredientID) references Ingredient(ingredientID) on delete cascade,foreign key(userID) references User(userID) on delete cascade);CREATE TABLE uses(measurementID int,ingredientID int,primary key(measurementID, ingredientID),foreign key(measurementID) references Measurement(measurementID) on delete cascade,foreign key(ingredientID) references Ingredient(ingredientID) on delete cascade);CREATE TABLE contains(recipeID int,ingredientID int,primary key(recipeID, ingredientID),foreign key(recipeID) references Recipe(recipeID) on delete cascade,foreign key(ingredientID) references Ingredient(ingredientID) on delete cascade);CREATE TABLE creates(recipeID int,mealID int,primary key(recipeID, mealID),foreign key(recipeID) references Recipe(recipeID) on delete cascade,foreign key(mealID) references Meal(mealID) on delete cascade);CREATE TABLE adds(recipeID int,userID int,primary key(userID, recipeID),foreign key(recipeID) references Recipe(recipeID) on delete cascade,foreign key(userID) references User(userID) on delete cascade);/*======================================INSERTING Measurements======================================*//*======================================INSERTING Ingredients======================================*//*======================================INSERTING 250,000 USERS======================================*//*======================================INSERTING 600,000 Recipes and Connections======================================*//*======================================INSERTING Meals======================================*//*======================================INSERTING Relationship: creates======================================*//*======================================INSERTING Relationship: contains======================================*//*======================================INSERTING Instructions======================================*//*======================================INSERTING Relationship: adds======================================*//*======================================INSERTING Relationship: kitchen======================================*/